import React from 'react';
import img1 from './head-img1.jpg';
import './Head.css';
import 'react-date-range/dist/styles.css'; // main style file
import 'react-date-range/dist/theme/default.css'; // theme css file
import { DateRange } from 'react-date-range';
import { useState } from "react";
import { format } from 'date-fns';
import {
    faBed,
    faCalendarDays,
    faPerson
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const Navbar = () => {
    const [openDate, setOpen] = useState(false)
    const [date, setState] = useState([
        {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection'
        }
    ]);

    const [numRooms, setNumRooms] = useState(1);
    const [numAdults, setNumAdults] = useState(1);
    const [numChildren, setNumChildren] = useState(0);

    const handleNumRoomsChange = (event) => {
        setNumRooms(parseInt(event.target.value));
    };

    const handleNumAdultsChange = (event) => {
        setNumAdults(parseInt(event.target.value));
    };

    const handleNumChildrenChange = (event) => {
        setNumChildren(parseInt(event.target.value));
    };

    return (

        <div className="head">
            <div
                className="">
                <img
                    class="rounded"
                    src={img1}
                    alt="" />
                <div className='search'>
                    <div className="bookItems">
                        <FontAwesomeIcon icon={faBed} className='icon' />
                        <input
                            type="text"
                            placeholder="Destination"
                            className="headerSearchInput"

                        />
                    </div>
                    <div className="bookItems">
                    <FontAwesomeIcon icon={faCalendarDays} className='icon' />
                        <span onClick={() => setOpen(!openDate)}>{`${format(date[0].startDate, "MM/dd/yyyy")} to ${format(date[0].endDate, "MM/dd/yyyy")}`}</span>
                        {openDate && <DateRange
                            onChange={item => setState([item.selection])}
                            showSelectionPreview={true}
                            moveRangeOnFirstSelection={false}
                            months={1}
                            ranges={date}
                            className='dates'
                        />}
                    </div>

                    <div className="bookItems">
                    <FontAwesomeIcon icon={faPerson} className='icon' />
                        <span>Rooms:<select value={numRooms} onChange={handleNumRoomsChange}>
                            {[...Array(10)].map((_, i) => (
                                <option key={i} value={i + 1}>
                                    {i + 1}
                                </option>
                            ))}
                        </select>
                            Adults:
                            <select value={numAdults} onChange={handleNumAdultsChange}>
                                {[...Array(10)].map((_, i) => (
                                    <option key={i} value={i + 1}>
                                        {i + 1}
                                    </option>
                                ))}
                            </select>
                            Children:
                            <select value={numChildren} onChange={handleNumChildrenChange}>
                                {[...Array(10)].map((_, i) => (
                                    <option key={i} value={i}>
                                        {i}
                                    </option>
                                ))}
                            </select></span>
                    </div>

                    <div className="bookItems">
                        <button class="bg-navy-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded ml-1">
                            Search
                        </button>
                    </div>
                </div>

            </div>
        </div>


    )

}

export default Navbar
